import React, { useState } from 'react';
import { useImages } from '../context/ImageContext';
import Navbar from '../components/common/Navbar';

const ImageSearch = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [error, setError] = useState('');
  const { searchSimilarImages, searchResults, loading } = useImages();

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    
    if (!selectedFile) {
      setFile(null);
      setPreview(null);
      return;
    }
    
    // Check if file is an image
    if (!selectedFile.type.startsWith('image/')) {
      setError('Please select an image file');
      setFile(null);
      setPreview(null);
      return;
    }
    
    setFile(selectedFile);
    setError('');
    
    // Create preview
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreview(reader.result);
    };
    reader.readAsDataURL(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Please select an image to search');
      return;
    }
    
    try {
      const response = await searchSimilarImages(file);
      const responseData = typeof response === 'string' ? JSON.parse(response) : response;

    } catch (err) {
      setError(err.response?.data?.error || 'Failed to search for similar images');
    }
  };

  return (
    <div>
      <Navbar />
      
      <div className="container">
        <div className="card">
          <h2>Search Similar Images</h2>
          <p>Upload an image to find similar images in the database.</p>
          
          {error && (
            <div className="alert alert-danger">{error}</div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="image">Select Image</label>
              <input
                type="file"
                id="image"
                className="form-control"
                accept="image/*"
                onChange={handleFileChange}
                disabled={loading}
              />
            </div>
            
            {preview && (
              <div style={{ marginTop: '20px', marginBottom: '20px' }}>
                <h3>Preview</h3>
                <img 
                  src={preview} 
                  alt="Preview" 
                  style={{ 
                    maxWidth: '100%', 
                    maxHeight: '300px', 
                    display: 'block',
                    margin: '0 auto',
                    borderRadius: '8px'
                  }} 
                />
              </div>
            )}
            
            <button 
              type="submit" 
              className="btn" 
              disabled={!file || loading}
            >
              {loading ? 'Searching...' : 'Search Similar Images'}
            </button>
          </form>
        </div>
        
        {loading && (
          <div className="spinner" style={{ marginTop: '30px' }}></div>
        )}
        
        {searchResults && searchResults.length > 0 && (
          <div style={{ marginTop: '30px' }}>
            <h3>Search Results</h3>
            <div className="image-grid">
              {searchResults.map((result, index) => {
                const ipfsUrl = `http://localhost:8080/ipfs/${result.ipfsHash}`;
                return (
                  <div key={index} className="image-card">
                    <img 
                      src={ipfsUrl}
                      alt={`Similar ${result.id}`}
                      style={{ 
                        width: '100%', 
                        height: '200px', 
                        objectFit: 'cover',
                        borderTopLeftRadius: '8px',
                        borderTopRightRadius: '8px'
                      }}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
                      }}
                    />
                    
                    <div className="image-info">
                      <h3>Image #{result.id}</h3>
                      <p>IPFS Hash: {result.ipfsHash.substring(0, 10)}...</p>
                      <p>Similarity: {Math.round(result.similarity * 100)}%</p>
                      
                      <div className="image-actions">
                        <a 
                          href={ipfsUrl} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="btn"
                        >
                          View on IPFS
                        </a>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}
        
        {searchResults && searchResults.length === 0 && !loading && (
          <div className="card" style={{ marginTop: '30px' }}>
            <p>No similar images found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ImageSearch;
