import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useImages } from '../context/ImageContext';
import Navbar from '../components/common/Navbar';
import { getIpfsUrl, getBestIpfsUrl } from '../util/ipfs';

const Dashboard = () => {
  const { user } = useAuth();
  const { 
    myImages, 
    loading, 
    error, 
    pagination,
    getMyImages, 
    getImageDownloadLink,
    downloadImageFile,
    pinAllImages,
    pinImage,
    unpinImage,
    runGarbageCollection,
    runSelectiveGarbageCollection,
    nextPage,
    previousPage,
    goToPage
  } = useImages();

  // Add state to track loading status of individual images
  const [imageLoadingStates, setImageLoadingStates] = useState({});

  // Queue for limiting concurrent API requests
  const requestQueue = React.useRef({
    queue: [],
    running: 0,
    maxConcurrent: 2, // Limit to 2 concurrent requests
    
    add(fn) {
      this.queue.push(fn);
      this.processQueue();
    },
    
    async processQueue() {
      if (this.running >= this.maxConcurrent || this.queue.length === 0) {
        return;
      }
      
      this.running++;
      const nextFn = this.queue.shift();
      
      try {
        await nextFn();
      } catch (error) {
        console.error("Error in queue processing:", error);
      } finally {
        this.running--;
        this.processQueue();
      }
    }
  });

  useEffect(() => {
    // Fetch user's images when component mounts and preload them
    const loadAndPreloadImages = async () => {
      try {
        // Load the first page of images (or the current page if refreshing)
        const response = await getMyImages(pagination.currentPage, pagination.pageSize);
        const images = response.results || [];
        
        // Preload images sequentially using the request queue to limit concurrent requests
        images.forEach(image => {
          requestQueue.current.add(async () => {
            setImageLoadingStates(prev => ({ ...prev, [image.id]: true }));
            try {
              // Get download link which will attempt to pin if not pinned
              await getImageDownloadLink(image.id);
              console.log(`Preloaded image ${image.id}`);
            } catch (err) {
              console.error(`Failed to preload image ${image.id}:`, err);
            } finally {
              setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
            }
          });
        });
      } catch (err) {
        console.error('Failed to load images:', err);
      }
    };
    
    loadAndPreloadImages();
  }, [
    pagination.currentPage,
    pagination.pageSize,
    getMyImages,
    getImageDownloadLink
  ]); // Include all dependencies

  const handleDownload = async (imageId) => {
    try {
      // First try the direct download method
      console.log(`Attempting direct download for image ${imageId}`);
      setImageLoadingStates(prev => ({ ...prev, [imageId]: true }));
      
      const directResult = await downloadImageFile(imageId);
      
      if (directResult.success) {
        console.log(`Direct download successful for image ${imageId}`);
        
        // If we got an objectUrl (blob), create a download link
        if (directResult.objectUrl) {
          // Create a temporary anchor element to trigger download
          const link = document.createElement('a');
          link.href = directResult.objectUrl;
          link.setAttribute('download', `image-${imageId}.jpg`);
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
          
          // Clean up the object URL to avoid memory leaks
          setTimeout(() => URL.revokeObjectURL(directResult.objectUrl), 100);
          
          console.log(`Download initiated for image ${imageId} using blob`);
        } 
        // If we got a fallback URL, open it in a new tab
        else if (directResult.url) {
          window.open(directResult.url, '_blank');
          console.log(`Download initiated for image ${imageId} using fallback URL`);
        }
      } else {
        // If direct download failed, fall back to the old method
        console.log(`Direct download failed for image ${imageId}, falling back to link method`);
        
        const response = await getImageDownloadLink(imageId);
        console.log(`Download link response for image ${imageId}:`, response);
        
        // Get the image from our state
        const image = myImages.find(img => img.id === imageId);
        if (!image) {
          console.error(`Image ${imageId} not found in state`);
          return;
        }
        
        // Get the best available URL (local or public)
        let downloadUrl;
        if (response.data && response.data.ipfsUrls && response.data.ipfsUrls.local) {
          downloadUrl = response.data.ipfsUrls.local;
          console.log(`Using local URL from response: ${downloadUrl}`);
        } else if (response.data && response.data.ipfsUrl) {
          // Fallback to the original URL if the new structure isn't available
          downloadUrl = response.data.ipfsUrl;
          console.log(`Using original URL from response: ${downloadUrl}`);
        } else {
          // If no URL is available, construct one from the hash using our utility
          const hash = (response.data && response.data.ipfsHash) || image.ipfs_hash;
          downloadUrl = getIpfsUrl(hash);
          console.log(`Constructed URL from hash: ${downloadUrl}`);
        }
        
        // Create a temporary anchor element to trigger download
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.setAttribute('download', `image-${imageId}.jpg`);
        link.setAttribute('target', '_blank');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        
        console.log(`Download initiated for image ${imageId} using fallback method`);
      }
    } catch (err) {
      console.error('Failed to download image:', err);
      alert('Failed to download image. Please try again later.');
    } finally {
      setImageLoadingStates(prev => ({ ...prev, [imageId]: false }));
    }
  };

  return (
    <div>
      <Navbar />
      
      <div className="container">
        <div className="card">
          <h2>Welcome, {user?.username}!</h2>
          <p>Role: {user?.role}</p>
          
          <div style={{ marginTop: '20px' }}>
            <Link to="/upload" className="btn">Upload New Image</Link>
            <Link to="/search" className="btn" style={{ marginLeft: '10px' }}>Search Similar Images</Link>
            <Link to="/analytics" className="btn" style={{ marginLeft: '10px' }}>Analytics</Link>
            <button 
              className="btn" 
              style={{ marginLeft: '10px' }}
              onClick={async () => {
                try {
                  const result = await pinAllImages();
                  alert(`Pin operation completed: ${result.newly_pinned} images newly pinned, ${result.already_pinned} were already pinned, ${result.failed_pins} failed.`);
                } catch (err) {
                  console.error('Failed to pin images:', err);
                  alert('Failed to pin images. Please try again later.');
                }
              }}
            >
              Pin All My Images
            </button>
            {user?.role === 'admin' && (
              <>
                <button 
                  className="btn" 
                  style={{ marginLeft: '10px', backgroundColor: '#f0ad4e' }}
                  onClick={async () => {
                    if (window.confirm('Are you sure you want to run garbage collection? This will remove all unpinned content from the IPFS repository.')) {
                      try {
                        const result = await runGarbageCollection();
                        alert(`Garbage collection completed successfully. Initial size: ${result.initial_size}, Final size: ${result.final_size}`);
                      } catch (err) {
                        console.error('Failed to run garbage collection:', err);
                        alert('Failed to run garbage collection. Please try again later.');
                      }
                    }
                  }}
                >
                  Reset IPFS Cache
                </button>
                <button 
                  className="btn" 
                  style={{ marginLeft: '10px', backgroundColor: '#17a2b8' }}
                  onClick={() => {
                    // Get date range for selective garbage collection
                    const startDate = prompt('Enter start date (YYYY-MM-DD) or leave empty:');
                    if (startDate === null) return; // User cancelled
                    
                    const endDate = prompt('Enter end date (YYYY-MM-DD) or leave empty:');
                    if (endDate === null) return; // User cancelled
                    
                    const params = {};
                    if (startDate) params.start_date = startDate;
                    if (endDate) params.end_date = endDate;
                    
                    if (window.confirm(`Are you sure you want to run selective garbage collection${startDate ? ` from ${startDate}` : ''}${endDate ? ` to ${endDate}` : ''}? This will keep pinned only the images within the specified date range.`)) {
                      try {
                        runSelectiveGarbageCollection(params)
                          .then(result => {
                            alert(`Selective garbage collection completed successfully. Kept ${result.kept_pins} images pinned, unpinned ${result.unpinned} images.`);
                          })
                          .catch(err => {
                            console.error('Failed to run selective garbage collection:', err);
                            alert('Failed to run selective garbage collection. Please try again later.');
                          });
                      } catch (err) {
                        console.error('Failed to run selective garbage collection:', err);
                        alert('Failed to run selective garbage collection. Please try again later.');
                      }
                    }
                  }}
                >
                  Selective GC
                </button>
              </>
            )}
          </div>
        </div>
        
        <h3 style={{ marginTop: '30px' }}>My Images</h3>
        
        {loading && (
          <div className="spinner"></div>
        )}
        
        {error && (
          <div className="alert alert-danger">{error}</div>
        )}
        
        {!loading && myImages.length === 0 && (
          <div className="card">
            <p>You haven't uploaded any images yet.</p>
          </div>
        )}
        
        <div className="image-grid" style={{ marginBottom: '20px' }}>
          {myImages.map((image) => (
            <div key={image.id} className="image-card">
              <div style={{ position: 'relative' }}>
                {imageLoadingStates[image.id] ? (
                  <div 
                    style={{ 
                      width: '100%', 
                      height: '200px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f0f0f0',
                      borderTopLeftRadius: '8px',
                      borderTopRightRadius: '8px'
                    }}
                  >
                    <div className="spinner" style={{ width: '40px', height: '40px' }}></div>
                  </div>
                ) : (
                  <img 
                    id={`img-${image.id}`}
                    src={image.ipfs_hash ? getIpfsUrl(image.ipfs_hash, true) : 'https://via.placeholder.com/300x200?text=No+IPFS+Hash'} // Handle null ipfs_hash
                    alt={`ID: ${image.id}`}
                    style={{ 
                      width: '100%', 
                      height: '200px', 
                      objectFit: 'cover',
                      borderTopLeftRadius: '8px',
                      borderTopRightRadius: '8px'
                    }}
                    onError={(e) => {
                      console.log(`Image ${image.id} failed to load, attempting to recover...`);
                      
                      // Set loading state
                      setImageLoadingStates(prev => ({ ...prev, [image.id]: true }));
                      
                      // Use getBestIpfsUrl with more retries if ipfs_hash exists
                      if (image.ipfs_hash) {
                        getBestIpfsUrl(image.ipfs_hash, 5)
                          .then(bestUrl => {
                            console.log(`Got best URL for image ${image.id}: ${bestUrl}`);
                            e.target.src = bestUrl;
                            
                            // If that fails, try to pin and get a new URL
                            e.target.onerror = () => {
                              console.log(`Best URL failed for image ${image.id}, trying to pin...`);
                              getImageDownloadLink(image.id)
                                .then(response => {
                                  if (response.isPinned) {
                                    console.log(`Image ${image.id} is now pinned, retrying load...`);
                                    // If now pinned, retry loading the image with force reload
                                    e.target.src = getIpfsUrl(image.ipfs_hash, true);
                                    e.target.onerror = null; // Remove this error handler
                                  } else {
                                    console.log(`Image ${image.id} is still not pinned, showing placeholder`);
                                    // If still not pinned, show placeholder
                                    e.target.onerror = null;
                                    e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
                                  }
                                  setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                                })
                                .catch((err) => {
                                  console.error(`Error getting download link for image ${image.id}:`, err);
                                  // On error, show placeholder
                                  e.target.onerror = null;
                                  e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
                                  setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                                });
                            };
                          })
                          .catch(err => {
                            console.error(`Error getting best URL for image ${image.id}:`, err);
                            setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                          });
                      } else {
                        // If no IPFS hash, show placeholder immediately
                        e.target.src = 'https://via.placeholder.com/300x200?text=No+IPFS+Hash';
                        setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                      }
                    }}
                    onLoad={() => {
                      console.log(`Image ${image.id} loaded successfully`);
                      setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                    }}
                  />
                )}
                {/* Add a refresh button to retry loading the image */}
                <button 
                  style={{
                    position: 'absolute',
                    top: '5px',
                    right: '5px',
                    background: 'rgba(255, 255, 255, 0.7)',
                    border: 'none',
                    borderRadius: '50%',
                    width: '30px',
                    height: '30px',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}
                  onClick={() => {
                    // Set loading state
                    setImageLoadingStates(prev => ({ ...prev, [image.id]: true }));
                    
                    // Get download link to ensure image is pinned
                    getImageDownloadLink(image.id)
                      .then(async (response) => {
                        console.log(`Refresh requested for image ${image.id}, pin status:`, response.isPinned);
                        
                        // If not pinned, try to pin it
                        if (!response.isPinned) {
                          try {
                            await pinImage(image.id);
                            console.log(`Manually pinned image ${image.id}`);
                          } catch (err) {
                            console.error(`Error pinning image ${image.id}:`, err);
                          }
                        }
                        
                        // Force reload the image
                        const imgElement = document.getElementById(`img-${image.id}`);
                        if (imgElement) {
                          // Use getBestIpfsUrl to get the most reliable URL
                          const bestUrl = await getBestIpfsUrl(image.ipfs_hash, 5);
                          imgElement.src = bestUrl;
                        }
                        
                        setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                      })
                      .catch((err) => {
                        console.error(`Error refreshing image ${image.id}:`, err);
                        setImageLoadingStates(prev => ({ ...prev, [image.id]: false }));
                      });
                  }}
                  title="Refresh image"
                  disabled={imageLoadingStates[image.id]}
                >
                  {imageLoadingStates[image.id] ? '⌛' : '↻'}
                </button>
              </div>
                
              <div className="image-info">
                <h3>Image #{image.id}</h3>
                <p>Hash: {image.sha256_hash.substring(0, 10)}...</p>
                <p>Uploaded: {new Date(image.uploaded_at).toLocaleString()}</p>
                <p>Deepfake: {image.deepfake_label} ({Math.round(image.deepfake_confidence * 100)}%)</p>
                
                {/* Pin status indicator */}
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  marginBottom: '10px',
                  color: image.is_pinned ? '#28a745' : '#dc3545'
                }}>
                  <span style={{ 
                    display: 'inline-block', 
                    width: '10px', 
                    height: '10px', 
                    borderRadius: '50%', 
                    backgroundColor: image.is_pinned ? '#28a745' : '#dc3545',
                    marginRight: '5px'
                  }}></span>
                  <span>{image.is_pinned ? 'Pinned' : 'Not Pinned'}</span>
                </div>
                
                <div className="image-actions">
                  <button 
                    className="btn" 
                    onClick={() => handleDownload(image.id)}
                  >
                    Download
                  </button>
                  
                  {/* Pin/Unpin button */}
                  {image.is_pinned ? (
                    <button 
                      className="btn" 
                      style={{ marginLeft: '10px', backgroundColor: '#dc3545' }}
                      onClick={async () => {
                        try {
                          await unpinImage(image.id);
                          alert(`Image #${image.id} has been unpinned.`);
                        } catch (err) {
                          console.error('Failed to unpin image:', err);
                          alert('Failed to unpin image. Please try again later.');
                        }
                      }}
                    >
                      Unpin
                    </button>
                  ) : (
                    <button 
                      className="btn" 
                      style={{ marginLeft: '10px', backgroundColor: '#28a745' }}
                      onClick={async () => {
                        try {
                          await pinImage(image.id);
                          alert(`Image #${image.id} has been pinned.`);
                        } catch (err) {
                          console.error('Failed to pin image:', err);
                          alert('Failed to pin image. Please try again later.');
                        }
                      }}
                    >
                      Pin
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
        
        {/* Pagination Controls */}
        {!loading && myImages.length > 0 && (
          <div className="pagination-controls" style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            margin: '20px 0 40px' 
          }}>
            <button 
              className="btn" 
              onClick={previousPage} 
              disabled={!pagination.hasPrevious}
              style={{ 
                margin: '0 5px',
                opacity: pagination.hasPrevious ? 1 : 0.5,
                cursor: pagination.hasPrevious ? 'pointer' : 'not-allowed'
              }}
            >
              &laquo; Previous
            </button>
            
            <div style={{ margin: '0 15px' }}>
              {/* Page numbers */}
              {Array.from({ length: pagination.totalPages }, (_, i) => i + 1).map(page => (
                <button
                  key={page}
                  onClick={() => goToPage(page)}
                  style={{
                    margin: '0 5px',
                    padding: '5px 10px',
                    border: page === pagination.currentPage ? '2px solid #007bff' : '1px solid #ccc',
                    borderRadius: '4px',
                    background: page === pagination.currentPage ? '#e6f2ff' : 'white',
                    cursor: 'pointer'
                  }}
                >
                  {page}
                </button>
              ))}
            </div>
            
            <button 
              className="btn" 
              onClick={nextPage} 
              disabled={!pagination.hasNext}
              style={{ 
                margin: '0 5px',
                opacity: pagination.hasNext ? 1 : 0.5,
                cursor: pagination.hasNext ? 'pointer' : 'not-allowed'
              }}
            >
              Next &raquo;
            </button>
          </div>
        )}
        
        {/* Pagination Info */}
        {!loading && myImages.length > 0 && (
          <div style={{ textAlign: 'center', marginBottom: '30px', color: '#666' }}>
            Showing page {pagination.currentPage} of {pagination.totalPages} ({pagination.totalImages} total images)
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
