import React, { createContext, useState, useContext } from 'react';
import { 
  uploadImage as apiUploadImage,
  getMyImages as apiGetMyImages,
  getImageDownloadLink as apiGetImageDownloadLink,
  downloadImageFile as apiDownloadImageFile,
  searchSimilarImages as apiSearchSimilarImages,
  getAllImages as apiGetAllImages,
  deleteImage as apiDeleteImage,
  batchUploadImages as apiBatchUploadImages,
  pinAllImages as apiPinAllImages,
  pinImage as apiPinImage,
  unpinImage as apiUnpinImage,
  runGarbageCollection as apiRunGarbageCollection,
  runSelectiveGarbageCollection as apiRunSelectiveGarbageCollection,
  getIPFSPinStats as apiGetIPFSPinStats
} from '../api/images';

// Create the context
const ImageContext = createContext(null);

// Custom hook to use the image context
export const useImages = () => useContext(ImageContext);

export const ImageProvider = ({ children }) => {
  const [myImages, setMyImages] = useState([]);
  const [allImages, setAllImages] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Upload an image
  const uploadImage = async (imageFile) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiUploadImage(imageFile);
      // Add the new image to the myImages array
      setMyImages(prevImages => [response.data, ...prevImages]);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Image upload failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Batch upload multiple images
  const batchUploadImages = async (imageFiles) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiBatchUploadImages(imageFiles);
      
      // Refresh the images list to include the newly uploaded images
      await getMyImages();
      
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Batch image upload failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Get user's images
  const getMyImages = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiGetMyImages();
      setMyImages(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch images');
      throw err;
    } finally {
      setLoading(false);
    }
  };

// Get IPFS download link
const getImageDownloadLink = async (imageId) => {
  setLoading(true);
  setError(null);
  try {
    const response = await apiGetImageDownloadLink(imageId);
    
    // If the image is not pinned, try to pin it
    if (response.data && !response.data.isPinned) {
      console.log(`Image ${imageId} is not pinned, attempting to pin it...`);
      
      try {
        // Try to pin the image
        await pinImage(imageId);
        console.log(`Successfully pinned image ${imageId}`);
        
        // Get the download link again to get updated pin status
        const updatedResponse = await apiGetImageDownloadLink(imageId);
        return updatedResponse;
      } catch (pinError) {
        console.error(`Error pinning image ${imageId}:`, pinError);
        // Continue with the original response even if pinning failed
      }
    }
    
    // Return the full response with all URLs and blockchain data
    return response;
  } catch (err) {
    setError(err.response?.data?.error || 'Failed to get download link');
    throw err;
  } finally {
    setLoading(false);
  }
};

// Directly download image file from IPFS through the backend
const downloadImageFile = async (imageId) => {
  setLoading(true);
  setError(null);
  try {
    const response = await apiDownloadImageFile(imageId);
    
    // If we got a blob response, create an object URL for it
    if (response.blob) {
      const objectUrl = URL.createObjectURL(response.blob);
      return {
        success: true,
        objectUrl,
        contentType: response.contentType,
        blob: response.blob
      };
    } 
    // If we got a URL fallback
    else if (response.url) {
      return {
        success: true,
        url: response.url,
        hash: response.hash
      };
    }
    // If we got an error
    else {
      setError(response.error || 'Failed to download image');
      return {
        success: false,
        error: response.error || 'Failed to download image'
      };
    }
  } catch (err) {
    const errorMsg = err.response?.data?.error || 'Failed to download image';
    setError(errorMsg);
    return {
      success: false,
      error: errorMsg
    };
  } finally {
    setLoading(false);
  }
};

  // Search for similar images
  const searchSimilarImages = async (imageFile) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiSearchSimilarImages(imageFile);
      setSearchResults(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Image search failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Admin: Get all images
  const getAllImages = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiGetAllImages();
      setAllImages(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch all images');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Admin: Delete an image
  const deleteImage = async (imageId) => {
    setLoading(true);
    setError(null);
    try {
      await apiDeleteImage(imageId);
      // Remove the deleted image from state
      setAllImages(prevImages => prevImages.filter(img => img.id !== imageId));
      setMyImages(prevImages => prevImages.filter(img => img.id !== imageId));
      return true;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete image');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Pin all images uploaded by the current user
  const pinAllImages = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiPinAllImages();
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to pin images');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Pin a specific image
  const pinImage = async (imageId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiPinImage(imageId);
      // Update the image in the state to reflect it's now pinned
      setMyImages(prevImages => 
        prevImages.map(img => 
          img.id === imageId ? { ...img, is_pinned: true } : img
        )
      );
      setAllImages(prevImages => 
        prevImages.map(img => 
          img.id === imageId ? { ...img, is_pinned: true } : img
        )
      );
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to pin image');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Unpin a specific image (admin only)
  const unpinImage = async (imageId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiUnpinImage(imageId);
      // Update the image in the state to reflect it's now unpinned
      setMyImages(prevImages => 
        prevImages.map(img => 
          img.id === imageId ? { ...img, is_pinned: false } : img
        )
      );
      setAllImages(prevImages => 
        prevImages.map(img => 
          img.id === imageId ? { ...img, is_pinned: false } : img
        )
      );
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to unpin image');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Run selective IPFS garbage collection (admin only)
  const runSelectiveGarbageCollection = async (params) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiRunSelectiveGarbageCollection(params);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to run selective garbage collection');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Get IPFS pin statistics (admin only)
  const getIPFSPinStats = async (params = {}) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiGetIPFSPinStats(params);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to get IPFS pin statistics');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Run IPFS garbage collection (admin only)
  const runGarbageCollection = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiRunGarbageCollection();
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to run garbage collection');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Context value
  const value = {
    myImages,
    allImages,
    searchResults,
    loading,
    error,
    uploadImage,
    batchUploadImages,
    getMyImages,
    getImageDownloadLink,
    downloadImageFile,
    searchSimilarImages,
    getAllImages,
    deleteImage,
    pinAllImages,
    pinImage,
    unpinImage,
    runGarbageCollection,
    runSelectiveGarbageCollection,
    getIPFSPinStats
  };

  return <ImageContext.Provider value={value}>{children}</ImageContext.Provider>;
};

export default ImageContext;
