import api from './index';

/**
 * Upload an image to the server
 * @param {File} imageFile - The image file to upload
 * @returns {Promise} - API response with the uploaded image data
 */
export const uploadImage = (imageFile) => {
  const formData = new FormData();
  formData.append('file', imageFile);
  return api.post('/api/images/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};



/**
 * Get IPFS download link for an image
 * @param {number} imageId - The ID of the image
 * @returns {Promise} - API response with the IPFS URLs and blockchain data
 */
export const getImageDownloadLink = async (imageId) => {
  try {
    const response = await api.get(`/api/images/download/${imageId}/`);
    
    // Return the full response data to allow access to all URLs
    return response.data;
  } catch (error) {
    console.error("Failed to get download link:", error);
    throw error;
  }
};

/**
 * Directly download an image file from IPFS through the backend
 * @param {number} imageId - The ID of the image to download
 * @returns {Promise} - API response with the image file or fallback URL
 */
export const downloadImageFile = async (imageId) => {
  try {
    // Use responseType 'blob' to handle binary data
    const response = await api.get(`/api/images/download-file/${imageId}/`, {
      responseType: 'blob'
    });
    
    // Check if the response is JSON (error) or binary (image)
    const contentType = response.headers['content-type'];
    
    if (contentType && contentType.includes('application/json')) {
      // Parse JSON error response
      const reader = new FileReader();
      return new Promise((resolve, reject) => {
        reader.onload = () => {
          try {
            const jsonResponse = JSON.parse(reader.result);
            resolve(jsonResponse);
          } catch (e) {
            reject(new Error('Invalid JSON response'));
          }
        };
        reader.onerror = reject;
        reader.readAsText(response.data);
      });
    } else {
      // Return the blob for image display
      return {
        blob: response.data,
        contentType: contentType || 'image/jpeg'
      };
    }
  } catch (error) {
    console.error("Failed to download image file:", error);
    throw error;
  }
};



/**
 * Admin: Get all images from all users
 * @returns {Promise} - API response with all images
 */
export const getAllImages = () => {
  return api.get('/api/images/admin/images/');
};

/**
 * Admin: Delete an image
 * @param {number} imageId - The ID of the image to delete
 * @returns {Promise} - API response confirming deletion
 */
export const deleteImage = (imageId) => {
  return api.delete(`/api/images/admin/images/${imageId}/`);
};
