import React, { createContext, useState, useContext, useCallback, useRef } from 'react';
import { 
  uploadImage as apiUploadImage,
  getMyImages as apiGetMyImages,
  getAllImages as apiGetAllImages,
  deleteImage as apiDeleteImage,
  getImageIpfsHash as apiGetImageIpfsHash
} from '../api/images';

// Create the context
const ImageContext = createContext(null);

// Custom hook to use the image context
export const useImages = () => useContext(ImageContext);

export const ImageProvider = ({ children }) => {
  const [myImages, setMyImages] = useState([]);
  const [allImages, setAllImages] = useState([]);
  const [ipfsHashes, setIpfsHashes] = useState({});

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalImages: 0,
    pageSize: 9,
    hasNext: false,
    hasPrevious: false
  });
  
  // Cache for API requests
  const requestCache = useRef({
    myImages: {}, // Cache for getMyImages requests

    cacheTimeout: 30000, // Cache timeout in ms (30 seconds)
    
    // Get cached data if available and not expired
    get(cacheKey, type) {
      const cache = this[type];
      const cachedData = cache[cacheKey];
      
      if (cachedData && Date.now() - cachedData.timestamp < this.cacheTimeout) {
        console.log(`Using cached data for ${type} - ${cacheKey}`);
        return cachedData.data;
      }
      
      return null;
    },
    
    // Store data in cache
    set(cacheKey, data, type) {
      this[type][cacheKey] = {
        data,
        timestamp: Date.now()
      };
    },
    
    // Clear specific cache entry
    clear(cacheKey, type) {
      if (this[type][cacheKey]) {
        delete this[type][cacheKey];
      }
    },
    
    // Clear all cache
    clearAll() {
      this.myImages = {};

    }
  });

  // Upload an image
  const uploadImage = useCallback(async (imageFile) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiUploadImage(imageFile);
      // Add the new image to the myImages array
      setMyImages(prevImages => [response.data, ...prevImages]);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Image upload failed');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);


  



  
// Admin: Get all images
const getAllImages = useCallback(async () => {
  setLoading(true);
  setError(null);
  try {
    const response = await apiGetAllImages();
    setAllImages(response.data);
    return response.data;
  } catch (err) {
    setError(err.response?.data?.error || 'Failed to fetch all images');
    throw err;
  } finally {
    setLoading(false);
  }
}, []); // Add comma here

// Admin: Delete an image
const deleteImage = useCallback(async (imageId) => {
  setLoading(true);
  setError(null);
  try {
    await apiDeleteImage(imageId);
    // Remove the deleted image from state
    setAllImages(prevImages => prevImages.filter(img => img.id !== imageId));
    setMyImages(prevImages => prevImages.filter(img => img.id !== imageId));
    return true;
  } catch (err) {
    setError(err.response?.data?.error || 'Failed to delete image');
    throw err;
  } finally {
    setLoading(false);
  }
}, []); // Add comma here

  
 

  // Get IPFS hash for an image
  const getImageIpfsHash = useCallback(async (imageId) => {
    // Check if we already have this hash cached
    if (ipfsHashes[imageId]) {
      return ipfsHashes[imageId];
    }
    
    try {
      const response = await apiGetImageIpfsHash(imageId);
      const hash = response.data.ipfs_hash;
      
      // Cache the hash
      setIpfsHashes(prev => ({
        ...prev,
        [imageId]: hash
      }));
      
      return hash;
    } catch (err) {
      console.error(`Failed to get IPFS hash for image ${imageId}:`, err);
      return null;
    }
  }, [ipfsHashes]);

  // Context value
  const value = {
    myImages,
    allImages,
    ipfsHashes,
    loading,
    error,
    uploadImage,
    getAllImages,
    deleteImage,
    getCacheStats,
    getImageIpfsHash
  };

  return <ImageContext.Provider value={value}>{children}</ImageContext.Provider>;
};

export default ImageContext;
