import React, { useEffect, useState } from 'react';
import { useMFS } from '../../context/MFSContext';

const MFSBrowser = () => {
  const {
    currentPath,
    directoryContents,
    loading,
    error,
    ls,
    mkdir,
    rm,
    navigateUp,
    navigateTo,
    getUrl
  } = useMFS();

  const [newDirName, setNewDirName] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  // Load directory contents on mount
  useEffect(() => {
    ls('/');
  }, [ls]);

  // Handle directory creation
  const handleCreateDirectory = async (e) => {
    e.preventDefault();
    if (!newDirName) return;

    try {
      const path = currentPath === '/' ? `/${newDirName}` : `${currentPath}/${newDirName}`;
      await mkdir(path);
      setNewDirName('');
      // Refresh directory listing
      ls(currentPath);
    } catch (err) {
      console.error('Failed to create directory:', err);
    }
  };

  // Handle file/directory deletion
  const handleDelete = async (path, isDirectory) => {
    if (!window.confirm(`Are you sure you want to delete ${path}?`)) return;

    try {
      await rm(path, isDirectory);
      // Refresh directory listing
      ls(currentPath);
      // Clear preview if the deleted file was selected
      if (selectedFile && selectedFile.path === path) {
        setSelectedFile(null);
        setPreviewUrl(null);
      }
    } catch (err) {
      console.error('Failed to delete:', err);
    }
  };

  // Handle file selection for preview
  const handleFileSelect = (file) => {
    setSelectedFile(file);
    
    // Generate preview URL for images
    if (file.type === 'file') {
      const path = file.path || (currentPath === '/' ? `/${file.name}` : `${currentPath}/${file.name}`);
      setPreviewUrl(getUrl(path));
    } else {
      setPreviewUrl(null);
    }
  };

  // Handle navigation to a directory
  const handleNavigate = (entry) => {
    const path = currentPath === '/' ? `/${entry.name}` : `${currentPath}/${entry.name}`;
    navigateTo(path);
  };

  // Render file/directory entry
  const renderEntry = (entry) => {
    const isDirectory = entry.type === 'directory';
    const path = currentPath === '/' ? `/${entry.name}` : `${currentPath}/${entry.name}`;
    
    return (
      <div 
        key={entry.name} 
        className={`mfs-entry ${isDirectory ? 'mfs-directory' : 'mfs-file'} ${selectedFile && selectedFile.name === entry.name ? 'selected' : ''}`}
        onClick={() => isDirectory ? handleNavigate(entry) : handleFileSelect({...entry, path})}
      >
        <div className="mfs-entry-icon">
          {isDirectory ? '📁' : '📄'}
        </div>
        <div className="mfs-entry-name">{entry.name}</div>
        <div className="mfs-entry-size">{entry.size}</div>
        <div className="mfs-entry-actions">
          <button onClick={(e) => { e.stopPropagation(); handleDelete(path, isDirectory); }}>
            Delete
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="mfs-browser">
      <h2>IPFS Mutable File System Browser</h2>
      
      {error && <div className="mfs-error">{error}</div>}
      
      <div className="mfs-toolbar">
        <div className="mfs-path">
          <button onClick={navigateUp} disabled={currentPath === '/'}>
            Up
          </button>
          <span>{currentPath}</span>
        </div>
        
        <form onSubmit={handleCreateDirectory} className="mfs-new-dir">
          <input
            type="text"
            placeholder="New directory name"
            value={newDirName}
            onChange={(e) => setNewDirName(e.target.value)}
          />
          <button type="submit">Create Directory</button>
        </form>
      </div>
      
      <div className="mfs-content">
        <div className="mfs-file-list">
          {loading ? (
            <div className="mfs-loading">Loading...</div>
          ) : directoryContents.length === 0 ? (
            <div className="mfs-empty">Directory is empty</div>
          ) : (
            directoryContents.map(entry => renderEntry(entry))
          )}
        </div>
        
        {selectedFile && (
          <div className="mfs-preview">
            <h3>{selectedFile.name}</h3>
            {previewUrl && selectedFile.name.match(/\.(jpg|jpeg|png|gif)$/i) ? (
              <img src={previewUrl} alt={selectedFile.name} />
            ) : (
              <div className="mfs-file-info">
                <p>Type: {selectedFile.type}</p>
                <p>Size: {selectedFile.size}</p>
                <p>Hash: {selectedFile.hash}</p>
                <a href={previewUrl} target="_blank" rel="noopener noreferrer">
                  Open/Download
                </a>
              </div>
            )}
          </div>
        )}
      </div>
      
      <style jsx>{`
        .mfs-browser {
          display: flex;
          flex-direction: column;
          height: 100%;
          padding: 1rem;
        }
        
        .mfs-toolbar {
          display: flex;
          justify-content: space-between;
          margin-bottom: 1rem;
        }
        
        .mfs-path {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        
        .mfs-content {
          display: flex;
          flex: 1;
          gap: 1rem;
        }
        
        .mfs-file-list {
          flex: 1;
          border: 1px solid #ccc;
          border-radius: 4px;
          overflow: auto;
        }
        
        .mfs-preview {
          flex: 1;
          border: 1px solid #ccc;
          border-radius: 4px;
          padding: 1rem;
          overflow: auto;
        }
        
        .mfs-entry {
          display: flex;
          align-items: center;
          padding: 0.5rem;
          cursor: pointer;
          border-bottom: 1px solid #eee;
        }
        
        .mfs-entry:hover {
          background-color: #f5f5f5;
        }
        
        .mfs-entry.selected {
          background-color: #e0f0ff;
        }
        
        .mfs-entry-icon {
          margin-right: 0.5rem;
        }
        
        .mfs-entry-name {
          flex: 1;
        }
        
        .mfs-entry-size {
          margin-right: 1rem;
          color: #666;
        }
        
        .mfs-directory {
          font-weight: bold;
        }
        
        .mfs-error {
          color: red;
          margin-bottom: 1rem;
        }
        
        .mfs-loading, .mfs-empty {
          padding: 1rem;
          text-align: center;
          color: #666;
        }
        
        img {
          max-width: 100%;
        }
      `}</style>
    </div>
  );
};

export default MFSBrowser;
