import React, { createContext, useState, useContext } from 'react';
import { 
  uploadImage as apiUploadImage,
  getMyImages as apiGetMyImages,
  getImageDownloadLink as apiGetImageDownloadLink,
  searchSimilarImages as apiSearchSimilarImages,
  getAllImages as apiGetAllImages,
  deleteImage as apiDeleteImage
} from '../api/images';

// Create the context
const ImageContext = createContext(null);

// Custom hook to use the image context
export const useImages = () => useContext(ImageContext);

export const ImageProvider = ({ children }) => {
  const [myImages, setMyImages] = useState([]);
  const [allImages, setAllImages] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Upload an image
  const uploadImage = async (imageFile) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiUploadImage(imageFile);
      // Add the new image to the myImages array
      setMyImages(prevImages => [response.data, ...prevImages]);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Image upload failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Get user's images
  const getMyImages = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiGetMyImages();
      setMyImages(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch images');
      throw err;
    } finally {
      setLoading(false);
    }
  };

 // Get IPFS download link
const getImageDownloadLink = async (imageId) => {
  setLoading(true);
  setError(null);
  try {
    const ipfsUrl = await apiGetImageDownloadLink(imageId);
    return ipfsUrl;
  } catch (err) {
    setError(err.response?.data?.error || 'Failed to get download link');
    throw err;
  } finally {
    setLoading(false);
  }
};

  // Search for similar images
  const searchSimilarImages = async (imageFile) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiSearchSimilarImages(imageFile);
      setSearchResults(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Image search failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Admin: Get all images
  const getAllImages = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiGetAllImages();
      setAllImages(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch all images');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Admin: Delete an image
  const deleteImage = async (imageId) => {
    setLoading(true);
    setError(null);
    try {
      await apiDeleteImage(imageId);
      // Remove the deleted image from state
      setAllImages(prevImages => prevImages.filter(img => img.id !== imageId));
      setMyImages(prevImages => prevImages.filter(img => img.id !== imageId));
      return true;
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete image');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Context value
  const value = {
    myImages,
    allImages,
    searchResults,
    loading,
    error,
    uploadImage,
    getMyImages,
    getImageDownloadLink,
    searchSimilarImages,
    getAllImages,
    deleteImage,
  };

  return <ImageContext.Provider value={value}>{children}</ImageContext.Provider>;
};

export default ImageContext;
