import { normalizeIpfsHash } from '../util/ipfs';
import api from './index';

/**
 * Get the URL for an image by its ID
 * @param {number} imageId - The ID of the image
 * @returns {string} - The URL to access the image
 */
export const getImageUrl = (imageId) => {
  return `${api.defaults.baseURL}/api/images/${imageId}/file/`;
};

/**
 * Get the IPFS hash for an image by its ID
 * @param {number} imageId - The ID of the image
 * @returns {Promise} - API response with the IPFS hash
 */
export const getImageIpfsHash = (imageId) => {
  return api.get(`/api/images/${imageId}/ipfs-hash/`);
};

/**
 * Upload an image to the server
 * @param {File} imageFile - The image file to upload
 * @returns {Promise} - API response with the uploaded image data
 */
export const uploadImage = (imageFile) => {
  const formData = new FormData();
  formData.append('file', imageFile);
  return api.post('/api/images/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};





/**
 * Admin: Get all images from all users
 * @returns {Promise} - API response with all images
 */
export const getAllImages = () => {
  return api.get('/api/images/admin/images/');
};

/**
 * Admin: Delete an image
 * @param {number} imageId - The ID of the image to delete
 * @returns {Promise} - API response confirming deletion
 */
export const deleteImage = (imageId) => {
  return api.delete(`/api/images/admin/images/${imageId}/`);
};

/**
 * Admin: Get cache statistics
 * @returns {Promise} - API response with cache statistics
 */
export const getCacheStats = () => {
  return api.get('/api/images/admin/cache/stats/');
};

/**
 * Get the current user's images with pagination
 * @param {Object} params - Pagination parameters
 * @param {number} params.page - The page number to fetch
 * @param {number} params.page_size - Number of items per page
 * @returns {Promise} - API response with paginated images
 */
export const getMyImages = (params = { page: 1, page_size: 9 }) => {
  return api.get('/api/images/my-images/', { params });
};
