import React, { useState, useEffect, useCallback } from 'react';
import debounce from 'lodash/debounce';
import { useImages } from '../context/ImageContext';
import Navbar from '../components/common/Navbar';
import { getIpfsUrl } from '../util/ipfs';

const ImageSearch = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [error, setError] = useState('');
  const { searchSimilarImages, searchResults, loading, pinImage, unpinImage } = useImages();
  
  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const ITEMS_PER_PAGE = 6;
  const [displayedResults, setDisplayedResults] = useState([]);
  const [totalPages, setTotalPages] = useState(1);

  // Update displayed results when search results or pagination changes
  useEffect(() => {
    if (searchResults && searchResults.length > 0) {
      // Calculate total pages
      const total = Math.ceil(searchResults.length / ITEMS_PER_PAGE);
      setTotalPages(total);
      
      // Ensure current page is valid
      const validPage = Math.min(Math.max(1, currentPage), total);
      if (validPage !== currentPage) {
        setCurrentPage(validPage);
      }
      
      // Get current page items
      const startIndex = (validPage - 1) * ITEMS_PER_PAGE;
      const endIndex = Math.min(startIndex + ITEMS_PER_PAGE, searchResults.length);
      setDisplayedResults(searchResults.slice(startIndex, endIndex));
    } else {
      setDisplayedResults([]);
      setTotalPages(1);
      setCurrentPage(1);
    }
  }, [searchResults, currentPage]); // setState functions are stable and don't need to be dependencies

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    
    if (!selectedFile) {
      setFile(null);
      setPreview(null);
      return;
    }
    
    // Check if file is an image
    if (!selectedFile.type.startsWith('image/')) {
      setError('Please select an image file');
      setFile(null);
      setPreview(null);
      return;
    }
    
    setFile(selectedFile);
    setError('');
    
    // Create preview
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreview(reader.result);
    };
    reader.readAsDataURL(selectedFile);
  };

  // Debounced search function to prevent rapid-fire API calls
  const debouncedSearch = useCallback(
    debounce(async (file) => {
      try {
        await searchSimilarImages(file);
      } catch (err) {
        setError(err.response?.data?.error || 'Failed to search for similar images');
      }
    }, 500),
    [searchSimilarImages]
  );

  // Memoized callbacks
  const handleImageError = useCallback((e, result) => {
    // If image fails to load, try with force reload first
    console.log(`Search result image ${result.id} failed to load, attempting force reload...`);
    
    // First try with a force reload (add timestamp to URL)
    const forceReloadUrl = getIpfsUrl(result.ipfsHash, true);
    console.log(`Trying force reload URL: ${forceReloadUrl}`);
    
    // Create a temporary image to test if force reload works
    const tempImg = new Image();
    tempImg.onload = () => {
      console.log(`Force reload successful for image ${result.id}`);
      e.target.src = forceReloadUrl;
    };
    tempImg.onerror = () => {
      // If force reload fails, try to pin the image if we have an ID
      if (result.id) {
        console.log(`Force reload failed for image ${result.id}, trying to pin...`);
        pinImage(result.id)
          .then(() => {
            console.log(`Pinned image ${result.id}, retrying load...`);
            // If pinned, retry loading the image with force reload
            e.target.src = getIpfsUrl(result.ipfsHash, true);
          })
          .catch((err) => {
            console.error(`Error pinning image ${result.id}:`, err);
            // On error, show placeholder
            e.target.onerror = null;
            e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
          });
      } else {
        // If no ID available, just show placeholder
        console.log(`No ID available for image, showing placeholder`);
        e.target.onerror = null;
        e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
      }
    };
    tempImg.src = forceReloadUrl;
  }, [pinImage, getIpfsUrl]);

  const handleViewOnIPFS = useCallback((e, ipfsUrl) => {
    e.preventDefault();
    window.open(ipfsUrl, '_blank');
  }, []);

  const handleDownload = useCallback((e, ipfsUrl, resultId) => {
    e.preventDefault();
    const link = document.createElement('a');
    link.href = ipfsUrl;
    link.setAttribute('download', `image-${resultId}.jpg`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }, []);

  const handlePin = useCallback(
    debounce(async (resultId) => {
      try {
        await pinImage(resultId);
        alert(`Image #${resultId} has been pinned.`);
        // Refresh search results to update pin status
        if (file) await searchSimilarImages(file);
      } catch (err) {
        console.error('Failed to pin image:', err);
        alert('Failed to pin image. Please try again later.');
      }
    }, 500),
    [pinImage, searchSimilarImages, file]
  );

  const handleUnpin = useCallback(
    debounce(async (resultId) => {
      try {
        await unpinImage(resultId);
        alert(`Image #${resultId} has been unpinned.`);
        // Refresh search results to update pin status
        if (file) await searchSimilarImages(file);
      } catch (err) {
        console.error('Failed to unpin image:', err);
        alert('Failed to unpin image. Please try again later.');
      }
    }, 500),
    [unpinImage, searchSimilarImages, file]
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Please select an image to search');
      return;
    }
    
    debouncedSearch(file);
  };

  return (
    <div>
      <Navbar />
      
      <div className="container">
        <div className="card">
          <h2>Search Similar Images</h2>
          <p>Upload an image to find similar images in the database.</p>
          
          {error && (
            <div className="alert alert-danger">{error}</div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="image">Select Image</label>
              <input
                type="file"
                id="image"
                className="form-control"
                accept="image/*"
                onChange={handleFileChange}
                disabled={loading}
              />
            </div>
            
            {preview && (
              <div style={{ marginTop: '20px', marginBottom: '20px' }}>
                <h3>Preview</h3>
                <img 
                  src={preview} 
                  alt="Preview" 
                  style={{ 
                    maxWidth: '100%', 
                    maxHeight: '300px', 
                    display: 'block',
                    margin: '0 auto',
                    borderRadius: '8px'
                  }} 
                />
              </div>
            )}
            
            <button 
              type="submit" 
              className="btn" 
              disabled={!file || loading}
            >
              {loading ? 'Searching...' : 'Search Similar Images'}
            </button>
          </form>
        </div>
        
        {loading && (
          <div className="spinner" style={{ marginTop: '30px' }}></div>
        )}
        
        {searchResults && searchResults.length > 0 && (
          <div style={{ marginTop: '30px' }}>
            <h3>Search Results ({searchResults.length} images found)</h3>
            <div className="image-grid">
              {displayedResults.map((result, index) => {
                const ipfsUrl = getIpfsUrl(result.ipfsHash); // Use the utility function for consistent URLs
                
                return (
                  <div key={index} className="image-card">
                    <img 
                      src={ipfsUrl}
                      alt={`Similar ${result.id}`}
                      style={{ 
                        width: '100%', 
                        height: '200px', 
                        objectFit: 'cover',
                        borderTopLeftRadius: '8px',
                        borderTopRightRadius: '8px'
                      }}
                      onError={(e) => handleImageError(e, result)}
                    />
                    
                    <div className="image-info">
                      <h3>Image #{result.id}</h3>
                      <p>IPFS Hash: {result.ipfsHash.substring(0, 10)}...</p>
                      <p>Similarity: {Math.round(result.similarity * 100)}%</p>
                      
                      {/* Pin status indicator */}
                      <div style={{ 
                        display: 'flex', 
                        alignItems: 'center', 
                        marginBottom: '10px',
                        color: result.is_pinned ? '#28a745' : '#dc3545'
                      }}>
                        <span style={{ 
                          display: 'inline-block', 
                          width: '10px', 
                          height: '10px', 
                          borderRadius: '50%', 
                          backgroundColor: result.is_pinned ? '#28a745' : '#dc3545',
                          marginRight: '5px'
                        }}></span>
                        <span>{result.is_pinned ? 'Pinned' : 'Not Pinned'}</span>
                      </div>
                      
                      <div className="image-actions">
                        <button 
                          className="btn"
                          onClick={(e) => handleViewOnIPFS(e, ipfsUrl)}
                        >
                          View on IPFS
                        </button>
                        
                        <button 
                          className="btn" 
                          style={{ marginLeft: '10px' }}
                          onClick={(e) => handleDownload(e, ipfsUrl, result.id)}
                        >
                          Download
                        </button>
                        
                        {/* Pin/Unpin button */}
                        {result.is_pinned ? (
                          <button 
                            className="btn" 
                            style={{ marginLeft: '10px', backgroundColor: '#dc3545' }}
                            onClick={() => handleUnpin(result.id)}
                          >
                            Unpin
                          </button>
                        ) : (
                          <button 
                            className="btn" 
                            style={{ marginLeft: '10px', backgroundColor: '#28a745' }}
                            onClick={() => handlePin(result.id)}
                          >
                            Pin
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
            
            {/* Pagination Controls */}
            {totalPages > 1 && (
              <div className="pagination-controls" style={{ 
                display: 'flex', 
                justifyContent: 'center', 
                alignItems: 'center', 
                margin: '20px 0 40px' 
              }}>
                <button 
                  className="btn" 
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} 
                  disabled={currentPage === 1}
                  style={{ 
                    margin: '0 5px',
                    opacity: currentPage > 1 ? 1 : 0.5,
                    cursor: currentPage > 1 ? 'pointer' : 'not-allowed'
                  }}
                >
                  &laquo; Previous
                </button>
                
                <div style={{ margin: '0 15px' }}>
                  {/* Page numbers */}
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      onClick={() => setCurrentPage(page)}
                      style={{
                        margin: '0 5px',
                        padding: '5px 10px',
                        border: page === currentPage ? '2px solid #007bff' : '1px solid #ccc',
                        borderRadius: '4px',
                        background: page === currentPage ? '#e6f2ff' : 'white',
                        cursor: 'pointer'
                      }}
                    >
                      {page}
                    </button>
                  ))}
                </div>
                
                <button 
                  className="btn" 
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))} 
                  disabled={currentPage === totalPages}
                  style={{ 
                    margin: '0 5px',
                    opacity: currentPage < totalPages ? 1 : 0.5,
                    cursor: currentPage < totalPages ? 'pointer' : 'not-allowed'
                  }}
                >
                  Next &raquo;
                </button>
              </div>
            )}
            
            {/* Pagination Info */}
            {totalPages > 1 && (
              <div style={{ textAlign: 'center', marginBottom: '30px', color: '#666' }}>
                Showing page {currentPage} of {totalPages} ({searchResults.length} total images)
              </div>
            )}
          </div>
        )}
        
        {searchResults && searchResults.length === 0 && !loading && (
          <div className="card" style={{ marginTop: '30px' }}>
            <p>No similar images found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ImageSearch;
