import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useImages } from '../context/ImageContext';
import Navbar from '../components/common/Navbar';

const Dashboard = () => {
  const { user } = useAuth();
  const { myImages, loading, error, getMyImages, getImageDownloadLink } = useImages();

  useEffect(() => {
    // Fetch user's images when component mounts
    getMyImages();
  }, []);

  const handleDownload = async (imageId) => {
    try {
      const response = await getImageDownloadLink(imageId);
      
      // Get the best available URL (local or public)
      let downloadUrl;
      if (response.ipfsUrls) {
        downloadUrl = response.ipfsUrls.local;
      } else if (response.ipfsUrl) {
        // Fallback to the original URL if the new structure isn't available
        downloadUrl = response.ipfsUrl;
      } else {
        // If no URL is available, construct one from the hash
        const hash = response.ipfsHash || (myImages.find(img => img.id === imageId)?.ipfs_hash);
        downloadUrl = `http://localhost:8080/ipfs/${hash}`;
      }
      
      // Create a temporary anchor element to trigger download
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.setAttribute('download', `image-${imageId}.jpg`);
      link.setAttribute('target', '_blank');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Failed to get download link:', err);
    }
  };

  return (
    <div>
      <Navbar />
      
      <div className="container">
        <div className="card">
          <h2>Welcome, {user?.username}!</h2>
          <p>Role: {user?.role}</p>
          
          <div style={{ marginTop: '20px' }}>
            <Link to="/upload" className="btn">Upload New Image</Link>
            <Link to="/search" className="btn" style={{ marginLeft: '10px' }}>Search Similar Images</Link>
          </div>
        </div>
        
        <h3 style={{ marginTop: '30px' }}>My Images</h3>
        
        {loading && (
          <div className="spinner"></div>
        )}
        
        {error && (
          <div className="alert alert-danger">{error}</div>
        )}
        
        {!loading && myImages.length === 0 && (
          <div className="card">
            <p>You haven't uploaded any images yet.</p>
          </div>
        )}
        
        <div className="image-grid">
        {myImages.map((image) => (
          <div key={image.id} className="image-card">

            <div style={{ position: 'relative' }}>
              <img 
                id={`img-${image.id}`}
                src={`http://localhost:8080/ipfs/${typeof image.ipfs_hash === 'object' ? 
                  (image.ipfs_hash.cid || image.ipfs_hash.Hash || '') : 
                  image.ipfs_hash}`}
                alt={`ID: ${image.id}`}
                style={{ 
                  width: '100%', 
                  height: '200px', 
                  objectFit: 'cover',
                  borderTopLeftRadius: '8px',
                  borderTopRightRadius: '8px'
                }}
                onError={(e) => {
                  // If image fails to load, try to get the download link which will attempt to pin it
                  getImageDownloadLink(image.id)
                    .then(response => {
                      if (response.isPinned) {
                        // If now pinned, retry loading the image
                        e.target.src = `http://localhost:8080/ipfs/${typeof image.ipfs_hash === 'object' ? 
                          (image.ipfs_hash.cid || image.ipfs_hash.Hash || '') : 
                          image.ipfs_hash}?${new Date().getTime()}`; // Add timestamp to force reload
                      } else {
                        // If still not pinned, show placeholder
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
                      }
                    })
                    .catch(() => {
                      // On error, show placeholder
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/300x200?text=Image+Unavailable';
                    });
                }}
              />
              {/* Add a refresh button to retry loading the image */}
              <button 
                style={{
                  position: 'absolute',
                  top: '5px',
                  right: '5px',
                  background: 'rgba(255, 255, 255, 0.7)',
                  border: 'none',
                  borderRadius: '50%',
                  width: '30px',
                  height: '30px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
                onClick={() => {
                  // Get download link to ensure image is pinned
                  getImageDownloadLink(image.id)
                    .then(() => {
                      // Force reload the image
                      const imgElement = document.getElementById(`img-${image.id}`);
                      if (imgElement) {
                        imgElement.src = `http://localhost:8080/ipfs/${typeof image.ipfs_hash === 'object' ? 
                          (image.ipfs_hash.cid || image.ipfs_hash.Hash || '') : 
                          image.ipfs_hash}?${new Date().getTime()}`;
                      }
                    });
                }}
                title="Refresh image"
              >
                â†»
              </button>
            </div>
              
              <div className="image-info">
                <h3>Image #{image.id}</h3>
                <p>Hash: {image.sha256_hash.substring(0, 10)}...</p>
                <p>Uploaded: {new Date(image.uploaded_at).toLocaleString()}</p>
                <p>Deepfake: {image.deepfake_label} ({Math.round(image.deepfake_confidence * 100)}%)</p>
                
                <div className="image-actions">
                  <button 
                    className="btn" 
                    onClick={() => handleDownload(image.id)}
                  >
                    Download
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
