import React, { useEffect } from 'react';
import { useImages } from '../../context/ImageContext';
import { useAuth } from '../../context/AuthContext';
import Navbar from '../../components/common/Navbar';

const AdminDashboard = () => {
  const { user } = useAuth();
  const { 
    allImages, 
    loading, 
    error, 
    getAllImages, 
    deleteImage,
    getImageDownloadLink
  } = useImages();

  useEffect(() => {
    // Fetch all images when component mounts
    getAllImages();
  }, []);

  const handleDownload = async (imageId) => {
    try {
      const ipfsUrl = await getImageDownloadLink(imageId);
      window.open(ipfsUrl, '_blank');
    } catch (err) {
      console.error('Failed to get download link:', err);
    }
  };

  const handleDelete = async (imageId) => {
    if (window.confirm('Are you sure you want to delete this image?')) {
      try {
        await deleteImage(imageId);
      } catch (err) {
        console.error('Failed to delete image:', err);
      }
    }
  };

  return (
    <div>
      <Navbar />
      
      <div className="container">
        <div className="card">
          <h2>Admin Dashboard</h2>
          <p>Welcome, {user?.username}! You have admin privileges.</p>
        </div>
        
        <h3 style={{ marginTop: '30px' }}>All Images</h3>
        
        {loading && (
          <div className="spinner"></div>
        )}
        
        {error && (
          <div className="alert alert-danger">{error}</div>
        )}
        
        {!loading && allImages.length === 0 && (
          <div className="card">
            <p>No images found in the system.</p>
          </div>
        )}
        
        <div className="image-grid">
          {allImages.map((image) => (
            <div key={image.id} className="image-card">
              <img 
                src={`https://ipfs.io/ipfs/${image.ipfs_hash}`}
                alt={`Image ${image.id}`}
                style={{ 
                  width: '100%', 
                  height: '200px', 
                  objectFit: 'cover',
                  borderTopLeftRadius: '8px',
                  borderTopRightRadius: '8px'
                }}
              />
              
              <div className="image-info">
                <h3>Image #{image.id}</h3>
                <p>Hash: {image.sha256_hash.substring(0, 10)}...</p>
                <p>Uploaded by: User #{image.uploader}</p>
                <p>Uploaded: {new Date(image.uploaded_at).toLocaleString()}</p>
                <p>Deepfake: {image.deepfake_label} ({Math.round(image.deepfake_confidence * 100)}%)</p>
                
                <div className="image-actions">
                  <button 
                    className="btn" 
                    onClick={() => handleDownload(image.id)}
                    style={{ marginRight: '10px' }}
                  >
                    Download
                  </button>
                  <button 
                    className="btn btn-danger" 
                    onClick={() => handleDelete(image.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
