/**
 * Constructs a consistent IPFS URL for a given hash
 * @param {string} hash - The IPFS hash/CID
 * @param {boolean} useLocalGateway - Whether to use the local gateway (default: true)
 * @returns {string} - The complete IPFS URL
 */
export const getIpfsUrl = (hash, useLocalGateway = true) => {
    if (!hash) return '';
    
    // Use local gateway for development, public gateway for production
    const gateway = useLocalGateway 
      ? 'http://localhost:8080/ipfs/' 
      : 'https://ipfs.io/ipfs/';
      
    return `${gateway}${hash}`;
  };
